
public class Loop_Program {
	
		public static void main(String[] args) {
			// TODO Auto-generated method stub
 
			for (int i = 1; i<=5; i++)
			{
				System.out.println(i + "Hello World");
			}
				int i = 10;
				while (i >= -10)
				{
				System.out.println(i);
				i--;
				}
				do
				{
					System.out.println(i);
					i++;
				}
				while(i <=10);

				}
		}
 

